function toggleMenu(){let e=document.getElementById("nav-icon"),n=document.getElementById("nav-menu");e.classList.contains("open")?(e.classList.remove(["open"]),n.classList.remove(["open"])):(e.classList.add("open"),n.classList.add("open"))}function toggleVideo(){let e=document.getElementById("video"),n=document.getElementById("play-btn");e.paused?(e.play(),n.style.display="none"):(e.pause(),n.style.display="block")}